mapper.insert:
INSERT INTO orders (
    number, status, user_id, address_book_id, order_time, checkout_time,
    pay_method, pay_status, amount, remark, phone, address, consignee,
    estimated_delivery_time, delivery_status, pack_amount, tableware_number, tableware_status,user_name
)
VALUES (
    #{number}, #{status}, #{userId}, #{addressBookId}, #{orderTime}, #{checkoutTime},
    #{payMethod}, #{payStatus}, #{amount}, #{remark}, #{phone}, #{address}, #{consignee},
    #{estimatedDeliveryTime}, #{deliveryStatus}, #{packAmount}, #{tablewareNumber}, #{tablewareStatus},#{userName}
)

mapper.update:
update orders
cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, pay_status=#{payStatus}, pay_method=#{payMethod}, checkout_time=#{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}
where id = #{id}

mapper.select:
select * from orders


        user_id = #{userId}


        AND status = #{status}


        AND order_time >= #{beginTime}


        AND order_time <= #{endTime}


order by order_time desc

select count(id) from orders

     and order_time > #{begin} 
     and order_time < #{end} 
     and status = #{status}
